import NoteContext from "./noteContext";
import { useState } from "react";

const NoteState = (props) => {
    const host = "http://localhost:5000"
    const notesInitial = []
    const [notes, setNotes] = useState(notesInitial)

    // get all  NOTEs
    const getNote = async () => {
        const response = await fetch(`${host}/api/notes/fetchallnotes`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjQyY2ZkNjE4NDkxMmJkYmVmY2Q0ZDY4In0sImlhdCI6MTY4MDY3MDA0OX0.7rJN09MzSiZgrZmHpBYsAcmqKIL1lj2n3n8eMo8Lykc"
            }
        });
        const json = await response.json()
        setNotes(json)
    }

    // ADD A NOTE
    const addNote = async (title, content, tag) => {
        const response = await fetch(`${host}/api/notes/addnote`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjQyY2ZkNjE4NDkxMmJkYmVmY2Q0ZDY4In0sImlhdCI6MTY4MDY3MDA0OX0.7rJN09MzSiZgrZmHpBYsAcmqKIL1lj2n3n8eMo8Lykc"
            },
            body: JSON.stringify({ title, content, tag })
        });
        const json = response.json();
        console.log(json)
        const note = {
            "_id": "64297338b635a09775e6e6d7",
            "user": "64294cc356dcd291adgea4a9f",
            "title": title,
            "content": content,
            "tag": tag,
            "date": "2023-04-02T10:05:26.959Z",
            "__v": 0
        }
        setNotes(notes.concat(note))
    }
    // DELETE A NOTE
    const deleteNote = async (id) => {
        const response = await fetch(`${host}/api/notes/deletenote/${id}`, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json',
                "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjQyY2ZkNjE4NDkxMmJkYmVmY2Q0ZDY4In0sImlhdCI6MTY4MDY3MDA0OX0.7rJN09MzSiZgrZmHpBYsAcmqKIL1lj2n3n8eMo8Lykc"
            },
        });
        const json = response.json();
        console.log(json)
        console.log("delete note with id: " + id)
        const newNotes = notes.filter((note) => { return note._id !== id })
        setNotes(newNotes)
    }
    // EDIT A NOTE
    const editNote = async (id, title, content, tag) => {
        // Api Call
        const response = await fetch(`${host}/api/notes/updatenote/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjQyY2ZkNjE4NDkxMmJkYmVmY2Q0ZDY4In0sImlhdCI6MTY4MDY3MDA0OX0.7rJN09MzSiZgrZmHpBYsAcmqKIL1lj2n3n8eMo8Lykc"
            },
            body: JSON.stringify({ title, content, tag })
        });
        const json = response.json();
        console.log(json)
        let newNotes = JSON.parse()
        // Logic to edit in client
        for (let index = 0; index < notes.length; index++) {
            const element = notes[index];
            if (element._id === id) {
                notes[index].title = title;
                notes[index].content = content;
                notes[index].tag = tag;
            }
            break
        }
        console.log(notes)
        setNotes(notes);
    }
    return (
        <NoteContext.Provider value={{ notes, addNote, deleteNote, editNote, getNote }}>
            {props.children}
        </NoteContext.Provider>
    )
}

export default NoteState;
