import { React, useContext, useEffect, useRef } from 'react'
import NoteContext from '../context/notes/noteContext';
import NoteItem from './NoteItem';
import Addnote from './Addnote';

const Notes = (props) => {
    const context = useContext(NoteContext);
    const { notes, getNote } = context;
    useEffect(() => {
        getNote()
        // eslint-disable-next-line
    }, [])
    const ref = useRef(null)
    const updateNote = (note) => {
        ref.current.click();
    }
    return (
        <>
            <Addnote />
            <button type="button" ref={ref} className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
                Launch demo modal
            </button>
            <div className="modal fade" id="exampleModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">Modal title</h5>
                        </div>
                        <div className="modal-body">
                            <form>
                                <div className="mb-3">
                                    <label htmlFor="title" className="form-label">Title</label>
                                    <input type="text" className="form-control" id="title" name='title' aria-describedby="emailHelp" onChange={props.onChange} placeholder='Enter Note Title' />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="content" className="form-label">Content</label>
                                    <input type="text" className="form-control" id="content" name='content' onChange={props.onChange} placeholder='Enter Note Content' />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="tag" className="form-label">Tag</label>
                                    <input type="text" className="form-control" id="tag" name='tag' onChange={props.onChange} placeholder='Enter tag' />
                                </div>
                            </form>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-primary">Save changes</button>
                        </div>
                    </div>
                </div>
            </div>
            <div className="row my-3">
                <h3>Your Notes</h3>
                {notes.map((note) => {
                    return <NoteItem key={note._id} updateNote={updateNote} note={note} />
                })}
            </div>
        </>
    )
}

export default Notes
