import { React, useContext, useEffect, useRef } from 'react'
import NoteContext from '../context/notes/noteContext';
import NoteItem from './NoteItem';
import Addnote from './Addnote';

const Notes = (props) => {
    const context = useContext(NoteContext);
    const { notes, getNote } = context;
    useEffect(() => {
        getNote()
        // eslint-disable-next-line
    }, [])
    const updateNote = (note) => {
        ref.current.toggle()
    }
    const ref = useRef(null)
    return (
        <>
            <Addnote />
            {/* <button ref={ref} type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
                Launch demo modal
            </button> */}
            <div class="modal fade" id="exampleModal" tabindex="-1" ref={ref} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog" role="document">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLabel">Edit Note</h5>
                            <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div class="modal-body">
                            <form>
                                <div className="mb-3">
                                    <label htmlFor="etitle" className="form-label">Title</label>
                                    <input type="text" className="form-control" id="etitle" name='etitle' aria-describedby="emailHelp" onChange={props.onChange} placeholder='Enter Note Title' />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="econtent" className="form-label">Content</label>
                                    <input type="text" className="form-control" id="econtent" name='econtent' onChange={props.onChange} placeholder='Enter Note Content' />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="etag" className="form-label">Tag</label>
                                    <input type="text" className="form-control" id="etag" name='etag' onChange={props.onChange} placeholder='Enter tag' />
                                </div>
                            </form>
                        </div>
                        <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" class="btn btn-primary">Update Note</button>
                        </div>
                    </div>
                </div>
            </div>
            <div className="row my-3">
                <h3>Your Notes</h3>
                {notes.map((note) => {
                    return <NoteItem key={note._id} updateNote={updateNote} note={note} />
                })}
            </div>
        </>
    )
}

export default Notes
